package pers.mk.springcloud.payment;import org.slf4j.Logger;import org.slf4j.LoggerFactory;import org.springframework.boot.SpringApplication;import org.springframework.boot.autoconfigure.SpringBootApplication;import org.springframework.cloud.client.discovery.EnableDiscoveryClient;import org.springframework.cloud.netflix.eureka.EnableEurekaClient;@SpringBootApplication@EnableEurekaClient@EnableDiscoveryClientpublic class PaymentServiceApplication {    private static final Logger LOGGER = LoggerFactory.getLogger(PaymentServiceApplication.class);    public static void main(String[] args) {        SpringApplication.run(PaymentServiceApplication.class,args);        LOGGER.info(">>>>>>>>>>>>> PaymentServiceApplication Started~ >>>>>>>>>>>>>>>");    }}